<?phpsession_name('kAdmin');session_save_path('../tmp');session_start();if (!isset($_SESSION['user'])) {    header("Location: index.php");}function __autoload($class_name){    include_once '../inc/class.' . $class_name . '.inc.php';}$DB = new database;$DB->Select('payment', 'id,amount,payer', 'id = ' . $DB->Escape($_POST['id']));$amount = $DB->Select_Result[0]['amount'];$payer = $DB->Select_Result[0]['payer'];$id = $DB->Select_Result[0]['id'];$set['status'] = 1;$update = $DB->Update('payment', $set, 'id= ' . $id);if ($update) {    $DB->Select('customer', 'id,credit', 'id = ' . $payer);    $credit = $amount + $DB->Select_Result[0]['credit'];    unset($set);    $set['credit'] = $credit;    $update2 = $DB->Update('customer', $set, 'id= ' . $payer);    if ($update2) {        echo 1;    } else {        echo 0;    }} else {    echo 0;}?>