<?phpif (isset($_POST['sub'])) {    include_once 'inc/class.logindb.inc.php';    if (isset($_POST['username']) && isset($_POST['password'])) {        $DB = new logindb;        $postuser = $DB->Escape($_POST['username']);        $postpass = $DB->Escape($_POST['password']);        $DB->Select('customer', 'id,pass', "username = '" . $postuser . "'");        if ($DB->Select_Rows > 0) {            if ($postpass == $DB->Select_Result[0]['pass']) {                $config = parse_ini_file("global/config.ini");                session_name('kUser');                session_save_path('tmp');                session_start();                foreach ($config as $key => $value) {                    $_SESSION[$key] = $value;                }                $_SESSION['user'] = $_POST['username'];                $_SESSION['userid'] = $DB->Select_Result[0]['id'];                header("location: dashboard.php");            } else {                $status = '<div class="alert alert-danger"  role="alert">					نام کاربری یا رمز عبور وارده اشتباه است.					</div>';            }        } else {            $status = '<div class="alert alert-danger" role="alert">					نام کاربری یا رمز عبور وارده اشتباه است.					</div>';        }    }}?><!DOCTYPE html><html lang="en" dir="rtl"><head>    <meta charset="utf-8">    <title>سیستم صورت حساب -- ورود</title>    <meta name="description" content="مديريت صورتحساب كارانشان">    <meta name="author" content="كارانشان چارسوي فناوري">    <meta name="viewport" content="width=device-width, initial-scale=1">    <!-- Le HTML5 shim, for IE6-8 support of HTML elements -->    <!--[if lt IE 9]>    <script src="http://html5shim.googlecode.com/svn/trunk/html5.js"></script>    <![endif]-->    <!-- Le styles -->    <link href="css/bootstrap.rtl.min.css" rel="stylesheet">    <link href="css/bootstrap-theme.min.css" rel="stylesheet">    <link href="css/style.css" rel="stylesheet">    <script type="text/javascript" src="js/