<?phprequire("header.php");//checks session and login$page = 'p1';include "top.php";//includes html top and menu?><div class="page-header">    <h1>لیست صورتحساب ها</h1></div><div class="row">    <div class="col-lg-12">        <div class="alert-message error" id="error2" style="display:none;">            <p style="display:none;">خطایی در ارسال پارامتر ها به وجود آمده است! دوباره امتحان کنید .</p>        </div>        <table class="table table-striped">            <thead>            <tr>                <th class="hidden-xs">شماره فاکتور</th>                <th>عنوان فاکتور</th>                <th class="hidden-xs">تاریخ سررسید</th>                <th>وضعیت</th>                <th>جمع فاکتور</th>                <th>دستورها</th>            </tr>            </thead>            <tbody>            <?php            $DB = new database;            if (!isset($_GET['p']) || empty($_GET['p'])) {                $lim = 1;            } else {                $lim = $DB->Escape($_GET['p']);            }            if ($lim == 'All') {                $pagin = ' ';            } else {                $start = $lim * $_SESSION['resultperpage'];                $start -= $_SESSION['resultperpage'];                $end = $_SESSION['resultperpage'];                $pagin = 'LIMIT ' . $start . ',' . $end;            }            $DB->Select('invoice INNER JOIN customer on invoice.customer_id=customer.id ', '*,customer.name as cusName,invoice.id as inID,invoice.status as sts', 'type = 1 and customer_id = ' . $_SESSION['userid'] . ' order by invoice.status ASC, invoice.gen_date DESC ' . $pagin);            $totalRows = $DB->TotalRows;            $num = $DB->Select_Rows;            $i = 0;            while ($i < $num) {                ?>                <tr id="<?php echo 'a' . $DB->Select_Result[$i]['inID']; ?>">                    <th class="hidden-xs"><?php echo $DB->Select_Result[$i]['inID']; ?></th>                    <th>                        <a href="invoiceview.php?id=<?php echo $DB->Select_Result[$i]['inID']; ?>"><?php echo $DB->Select_Result[$i]['invoice_name']; ?></a>                    </th>                    <td class="hidden-xs"><?php echo infdate($DB->Select_Result[$i]['gen_date']); ?></td>                    <td><?php echo findStatus($DB->Select_Result[$i]['sts']); ?></td>                    <td><?php echo number_format(invoiceBalance($DB->Select_Result[$i]['inID'])); ?> ریال</td>                    <td><a href="invoiceview.php?id=<?php echo $DB->Select_Result[$i]['inID']; ?>"                           class="btn btn-xs btn-info hidden-xs">مشاهده</a>                        <?php if ($DB->Select_Result[$i]['status'] == 0) { ?>                            <a href="payinvoice.php?id=<?php echo $DB->Select_Result[$i]['inID']; ?>"                               class="btn btn-xs btn-success">پرداخت</a>                        <?php } ?></td>                </tr>                <?php                $i++;            } ?>            </tbody>        </table>        <nav class="lesspad">            <ul class="pagination">                <?php                $p = isset($_GET['p']) ? $_GET['p'] : 1;                $fdot = $ldot = 0;                $pages = ceil($totalRows / $_SESSION['resultperpage']);                if ($p > 1) {                    $href = $p - 1;                    echo '<li><a href="?p=' . $href . '"><span aria-hidden="true">&laquo;</span><span class="sr-only">قبلی</span></a></li>';                }                for ($i = 1; $i <= $pages; $i++) {                    if ($pages < 6 || $i == $pages || $i == 1 || ($i > $p - 3 && $i < $p + 3)) {                        $act = $i == $p ? 'class="active"' : ' ';                        echo '<li ' . $act . '><a href="?p=' . $i . '">' . $i . '</a></li>';                    } else {                        if ($fdot == 0) {                            echo '<li class="disabled"><a >...</a></li>';                            $fdot = 1;                        }                        if ($fdot == 1 && $i > $p && $ldot == 0 && $p >= 5) {                            echo '<li  class="disabled"><a >...</a></li>';                            $ldot = 1;                        }                    }                }                if ($p < $pages) {                    $href = $p + 1;                    echo '<li><a href="?p=' . $href . '"><span aria-hidden="true">&raquo;</span><span class="sr-only">بعدی</span></a></li>';                }                ?>            </ul>        </nav>        <div class="actions"><span class="totalRow"> جمع کل صورتحساب های معوقه: </span><span                class="totalRow"><?php echo number_format(userBalance($_SESSION['userid'])); ?> ریال</span>            <input type="button" value="تسویه کل بدهی " onClick="document.location.href='payinvoice.php'"                   class="btn btn-primary">        </div>    </div></div></div></div><?phpinclude("footer.php");?></body></html>